//锁定皮肤背景
function lock_background(This){
    var $this = $(This);

    var lock_status = 1;
    //选中复选框,锁定皮肤背景
    if($this.prop('checked')){
        lock_status = 2;
    }
    $.post(
        '/api/skin/lock_background',
        {
            lock_status: lock_status
        },
        function(json){
            if(json.error_code == 'ok'){
                if(lock_status == 1){
                    layer.msg('成功解除锁定背景图片。');
                    setTimeout(function(){
                        document.location = document.location;
                    }, 1000);
                }
                else if(lock_status == 2){
                    layer.msg('成功锁定背景图片。');
                    setTimeout(function(){
                        document.location = document.location;
                    }, 1000);
                }
            }
            else{
                show_error(json.error_code);
            }
        },
        'json'
    );
}