//删除消息
function msg_del(msg_id){
    layer.confirm('确定要删除这条消息吗？', {icon: 3, shade:0, title:'提示'}, function(index){
        layer.load();
        $.post(
            '/api/msg/del',
            {
                id: msg_id
            },
            function(json){
                layer.closeAll('loading');
                if(json.error_code == 'ok'){
                    $('#msg_' + msg_id).remove();
                    layer.msg('删除成功');
                }
                else{
                    show_error(json.error_code);
                }
            },
            'json'
        );
        layer.close(index);
    });
}