$(function(){
    var brief = $('#brief').val();
    $('#brief').val('');
    $('#brief').val(brief).focus();
});
//更新用户资料
function update_profile(){
    var $gender = $('#gender');
    var gender = $gender.val();
    var $brief = $('#brief');
    var brief = $brief.val();

    if(!simple_validate.mix_range(brief, 0, 140)){
        layer.msg('个人简介最大长度为140，1个中文算2个字符长度');
        return false;
    }

    $.post(
        '/api/account/update_profile',
        {
            gender: gender,
            brief: brief
        },
        function(json){
            if(json.error_code == 'ok'){
                layer.msg('更新成功');
                document.location = document.location;
            }
            else{
                show_error(json.error_code);
            }
        },
        'json'
    );
    return false;
}

$(function(){
    if(typeof layui != 'undefined'){
        layui.use('form', function(){
            var form = layui.form();
            form.render();
        });
    }
});